-- Create table for associating fees with classes
CREATE TABLE IF NOT EXISTS class_fees (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  classe VARCHAR NOT NULL,
  code_frais VARCHAR NOT NULL REFERENCES fees(code) ON DELETE CASCADE,
  created_at TIMESTAMPTZ DEFAULT now(),
  updated_at TIMESTAMPTZ DEFAULT now(),
  UNIQUE(classe, code_frais)
);

-- Enable RLS
ALTER TABLE class_fees ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "Allow all operations for authenticated users" ON class_fees
  FOR ALL USING (true);

-- Add periodicite column to fees table if it doesn't exist
DO $$ 
BEGIN
  IF NOT EXISTS (SELECT 1 FROM information_schema.columns 
                 WHERE table_name = 'fees' AND column_name = 'periodicite') THEN
    ALTER TABLE fees ADD COLUMN periodicite VARCHAR;
  END IF;
END $$;
